import {useState} from 'react';

const SongForm = ({onFormSubmit}) => {

    const [title, setTitle] = useState("")
    const [comment, setComment] = useState("")
    const [object, setObject] = useState({})

    const handleTitleChange = (event) => {
        setTitle(event.target.value)
    }

    const handleCommentChange = (event) => {
        setComment(event.target.value)
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        const titleToSubmit = title.trim()
        const commentToSubmit = comment.trim()
        const newSong = {title: titleToSubmit, comment: commentToSubmit}
        setObject([...object, newSong])
        setTitle ("")
        setComment("")
    }

    // const handleSubmit = (event) => {
    //     event.preventDefault();
    //     const titleToSubmit = title.trim()

    //     const updatedPlaylist = [...playlist, newSong]
    //     // setPlaylist(updatedPlaylist)
    //     setCurrentSong(newSong.songData)
    //     setSong([])
    // }

    return(
        <form>
             <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    placeholder="title" 
                    value={title}
                    onChange={handleTitleChange} 
                />
                <input 
                    type="text"
                    placeholder="Comment"
                    value={comment}
                    onChange={handleCommentChange}
                    />
                <input type="submit" value="POST" />
            </form>
        </form>
    )
}

export default SongForm;
