import * as Tone from 'tone';
import styled from 'styled-components';
import { useState } from 'react';

let Pad = styled.li` 
height: 6vh;
width: 8vw;
margin: auto;
border: 2px solid white;
color: white;
font-size: 15pt;
text-align: center;
box-shadow: 1px 3px 5px #C0C0C0;
`

const StyledPad = styled(Pad).attrs(props => ({
    style: {
        backgroundColor: (props.isLastPad) ? '#'+props.color : 'black'
    }
}))``

const Key = ({individualPad, onKeyClick, isLastPad}) => {

    return (
        <div onClick={onKeyClick}>
            <StyledPad color={individualPad.color} isLastPad={isLastPad}> {individualPad.keyPress} <br/> {individualPad.note} </StyledPad>
        </div>
    )
}

export default Key
