  const [playlist, setPlaylist] = useState([]);
  const [loaded, setLoaded] = useState(false);

  const fetchSongs = () => {
    console.log("getting songs info");
    SongService.getSongs()
        .then(data => setPlaylist(data))
        .then(() => setLoaded(true))
}

useEffect(() => {
  fetchSongs();
}, [])


